'use strict';

module.exports = {
	get: 'NOTIFICATION_GET',
	getParser: () => ({
		'V1 Alarm Type': 0,
		'Notification Type': 'Siren',
	}),
	report: 'NOTIFICATION_REPORT',
	reportParser: report => {
		if (report && report['Notification Type'] === 'Siren' && report.hasOwnProperty('Event')) {
			if (report['Event (Raw)'] === 1) return true;
			if (report['Event (Raw)'] === 0) return false;
		}
		return null;
	},
};